import React, { useState } from "react";
import AddBookForm from "./AddBookForm";
import EditBookModal from "./EditBookModal";
import DashboardLayout from "./DashboardLayout";

const Books = () => {
  const [books, setBooks] = useState([]);
  const [editBook, setEditBook] = useState(null);

  const addBook = (book) => {
    setBooks([...books, { id: books.length + 1, ...book }]);
  };

  const deleteBook = (id) => {
    setBooks(books.filter((book) => book.id !== id));
  };

  const openEditModal = (book) => {
    setEditBook(book);
  };

  const closeEditModal = () => {
    setEditBook(null);
  };

  const updateBook = (updatedBook) => {
    setBooks(
      books.map((book) => (book.id === updatedBook.id ? updatedBook : book))
    );
    closeEditModal();
  };

  return (
    <DashboardLayout>
      <div className="App container mx-auto">
        <AddBookForm addBook={addBook} />
        <div className="mt-10">
          <h2 className="text-2xl font-bold mb-4">Book List</h2>
          <ul className="space-y-4">
            {books.map((book) => (
              <li key={book.id} className="bg-white p-4 rounded-lg shadow-md">
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="text-xl font-bold">{book.title}</h3>
                    <p className="text-gray-700">{book.description}</p>
                  </div>
                  <div>
                    <button
                      onClick={() => openEditModal(book)}
                      className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mr-2"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => deleteBook(book.id)}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
        {editBook && (
          <EditBookModal
            book={editBook}
            updateBook={updateBook}
            closeModal={closeEditModal}
          />
        )}
      </div>
    </DashboardLayout>
  );
};

export default Books;
