// src/components/Sidebar.js
import React from "react";
import { Link } from "react-router-dom";
import { FaCog, FaHome, FaChartBar, FaUsers, FaLink } from "react-icons/fa";

const Sidebar = ({ isOpen, toggleSidebar }) => {
  return (
    <div
      className={`w-64 bg-white shadow-md ${
        isOpen ? "block" : "hidden"
      } md:block`}
    >
      <div className="p-4 border-b">
        <h2 className="text-lg font-semibold">User Name</h2>
        <p className="text-sm text-gray-600">user@example.com</p>
      </div>
      <nav className="flex-1 p-4">
        <Link
          to="/dashboard"
          className="block py-2.5 px-4 rounded hover:bg-gray-200 flex items-center"
        >
          <FaHome className="mr-2" /> Dashboard
        </Link>
        <Link
          to="/add_new_book"
          className="block py-2.5 px-4 rounded hover:bg-gray-200 flex items-center"
        >
          <FaChartBar className="mr-2" /> Add New Book
        </Link>
        <Link
          to="/books"
          className="block py-2.5 px-4 rounded hover:bg-gray-200 flex items-center"
        >
          <FaUsers className="mr-2" />
          Books
        </Link>
        {/* Add more links here */}
      </nav>
      <div className="p-4 border-t">
        <Link
          to="/admin/linkStudents"
          className="block py-2.5 px-4 rounded hover:bg-gray-200 flex items-center"
        >
          <FaLink className="mr-2" /> Link Students
        </Link>
      </div>
    </div>
  );
};

export default Sidebar;
