  const openEditModal = (book) => {
    setEditBook(book);
  };

  const closeEditModal = () => {
    setEditBook(null);
  };

  const updateBook = (updatedBook) => {
    setBooks(books.map(book => book.id === updatedBook.id ? updatedBook : book));
    closeEditModal();
