// src/App.js
import React, { useEffect, useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Login from "./components/Login";
import Dashboard from "./components/Dashboard";
import AddNewBook from "./components/AddNewBook";
import Books from "./components/Books";
import Cookies, { useCookies } from "react-cookie";
import axios from "axios"; // Import axios for HTTP requests

import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [cookies] = useCookies(["token"]); // Use the cookies hook
  const navigate = useNavigate();
  const token = cookies.token; // Get the token from cookies

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/token/validate", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (response.data.message === "Token is valid") {
          // Token is valid, you can update the state or proceed
          setData(response.data); // Set data or manage state if needed
        } else {
          // Token is not valid, redirect to login
          navigate("/");
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
        navigate("/"); // Redirect to login on error
      }
    };

    if (token) {
      fetchUserData();
    } else {
      navigate("/"); // Redirect to login if no token
    }
  }, [token, navigate]);

  return (
    <>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/add_new_book" element={<AddNewBook />} />
        <Route path="/books" element={<Books />} />
      </Routes>
    </>
  );
}

export default App;
