import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import DashboardLayout from "./DashboardLayout";

const Books = () => {
  const [books, setBooks] = useState([]);
  const [editBook, setEditBook] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');

  // Load books from API
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('/api/books');
        setBooks(response.data);
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, []);

  // Delete book
  const deleteBook = async (id) => {
    const token = Cookies.get("token");

    try {
      await axios.delete(`/api/books/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setBooks(books.filter(book => book.id !== id));
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  // Open edit modal
  const openEditModal = (book) => {
    setEditBook(book);
    setTitle(book.title);
    setDescription(book.description);
    setCategory(book.category);
  };

  // Close edit modal
  const closeEditModal = () => {
    setEditBook(null);
    setTitle('');
    setDescription('');
    setCategory('');
  };

  // Update book
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    const token = Cookies.get("token");

    try {
      const updatedBook = { ...editBook, title, description, category };
      await axios.put(`/api/books/${editBook.id}`, updatedBook, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setBooks(books.map(book => (book.id === editBook.id ? updatedBook : book)));
      closeEditModal();
    } catch (error) {
      console.error('Error updating book:', error);
    }
  };

  return (
    <DashboardLayout>
    <div className="container mx-auto">
      <div className="mt-10">
        <h2 className="text-2xl font-bold mb-4">Book List</h2>
        <ul className="space-y-4">
          {books.map(book => (
            <li key={book.id} className="bg-white p-4 rounded-lg shadow-md">
              <div className="flex justify-between items-center">
                <div>
                  <h3 className="text-xl font-bold">{book.title}</h3>
                  <p className="text-gray-700">{book.description}</p>
                  <p className="text-gray-500">{book.category}</p>
                </div>
                <div>
                  <button
                    onClick={() => openEditModal(book)}
                    className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mr-2"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => deleteBook(book.id)}
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  >
                    Delete
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>

      {editBook && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-75">
          <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
            <h2 className="text-2xl font-bold mb-6">Edit Book</h2>
            <form onSubmit={handleEditSubmit}>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="title">
                  Title
                </label>
                <input
                  type="text"
                  id="title"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="description">
                  Description
                </label>
                <textarea
                  id="description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="category">
                  Category
                </label>
                <input
                  type="text"
                  id="category"
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  required
                />
              </div>
              <div className="flex items-center justify-between">
                <button
                  type="submit"
                  className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  Update Book
                </button>
                <button
                  type="button"
                  onClick={closeEditModal}
                  className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
      </div>
      </DashboardLayout>
  );
};

export default Books;
