import React, { useContext, useEffect, useState } from "react";
import DashboardLayout from "./DashboardLayout";
import { FaUsers, FaChartLine, FaCog, FaFileAlt } from "react-icons/fa";
import Cookies from "js-cookie";

const Index = () => {
  const [toal_books, setTotalBooks] = useState(0);
  const token = Cookies.get("token");
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.post(
          `http://localhost:5000/api/Token/validate`,
          { token },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        console.log(response);
      } catch (error) {
        console.error("Error fetching user data:", error);
       
      }
    };

    fetchUserData();
  }, []);

  return (
    <div>
      <DashboardLayout>
        {/* Add your main content here */}
        <div></div>

        <div>
          <h1 className="text-2xl font-bold mb-4">Dashboard</h1>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div className="bg-white p-4 rounded-lg shadow-md border-b-4 border-blue-500">
              <div className="flex items-center">
                <FaUsers className="text-3xl text-blue-500 mr-4" />
                <div>
                  <p className="text-2xl font-semibold">{toal_books}</p>
                  <p className="text-gray-600">Total Books</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </DashboardLayout>
    </div>
  );
};

export default Index;
